public class zählen {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
	int x=0;
	while (x!=10){
		System.out.print(x);
		if (x==6){
			System.out.print(" - 6 ist vorhanden!");
		}
		x=x+1;
		System.out.println("");
		}
	}
	}
