public class acht_sept {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		long a=-1l;
		String str_1=Long.toBinaryString(a);
		int int_1=Long.numberOfLeadingZeros(a);
		int int_2=Long.numberOfTrailingZeros(a);
		System.out.println(str_1);
		System.out.println(int_1);
		System.out.println(int_2);
		int i=11;
		int j=5;
		System.out.println("---");
		System.out.println(i);
		System.out.println(j);
		i = ++j;
		System.out.println("---");
		System.out.println(i);
		System.out.println(j);
		
	}

}
